&#x27;)return c=getchar(),INF; while(isdigit(c))x=x*10+(c^48),c=getchar(); return w?-x:x; &#125; int n,m,val[maxn],ans; int ecnt=1,head[maxn],cur[maxn],to[maxm&lt;&lt;1],nxt[maxm&lt;&lt;1],w[maxm&lt;&lt;1]; inline void addedge(int a,int b,int c)&#123; to[++ecnt]=b,nxt[ecnt]=head[a],head[a]=ecnt,w[ecnt]=c; to[++ecnt]=a,nxt[ecnt]=head[b],head[b]=ecnt,w[ecnt]=0; &#125; int dep[maxn]; inline bool bfs()&#123; memset(dep,-1,sizeof dep); static int q[maxn]; int hd=0,tl=1; q[tl]=s,dep[s]=0; while(hd&lt;tl)&#123; int x=q[++hd]; for(int u,i=head[x];i;i=nxt[i]) if(dep[u=to[i]]==-1 and w[i]) dep[u]=dep[x]+1,q[++tl]=u; &#125; return dep[t]!=-1; &#125; int dfs(int x,int flow)&#123; if(x==t)return flow; int used=0,u; for(int&amp; i=cur[x];i;i=nxt[i]) if(dep[u=to[i]]==dep[x]+1 and w[i])&#123; int v=dfs(u,min(flow-used,w[i])); used+=v,w[i]-=v,w[i^1]+=v; if(used==flow) break; &#125; return used; &#125; inline int dinic()&#123; int ans=0; while(bfs()) memcpy(cur,head,sizeof cur),ans+=dfs(s,INF); return ans; &#125; inline void work()&#123; m=read(),n=read();read(); for(int v,i=1;i&lt;=m;i++)&#123; addedge(s,i,v=read());ans+=v; while((v=read())!=INF) addedge(i,v+m,INF); &#125; for(int i=1;i&lt;=n;i++) addedge(i+m,t,read()); ans-=dinic(); for(int i=1;i&lt;=m;i++) if(dep[i]!=-1) printf(&quot;%d &quot;,i);puts(&quot;&quot;); for(int i=1;i&lt;=n;i++) if(dep[m+i]!=-1) printf(&quot;%d &quot;,i); printf(&quot;
